import React from "react";
const Card = ({ pokemon, loading,infoPokemon}) => {
   // console.log(pokemon);
    return (
        <>    <select name="" id="">
        <option value=""> overgrowth</option>
        <option value=""> chlorophyll </option>
        <option value=""> torrent</option>
        <option value=""> swarm</option>
    </select>
        {
            loading ? <h1>Loading...</h1> :
                pokemon.slice(0.10).filter(e=>e.abilities.filter(el=>el.ability.name=="overgrow").length).map((item) => {
                    return (
                        <>
                            <div className="card" key={item.id} onClick={()=>infoPokemon(item)}>
                                <h2>{item.id}</h2>
                                <img src={item.sprites.front_default} alt="" />
                                <h2>{item.name}</h2>
                            </div>
                        </>
                    )
                })
        }

        </>
    )
}
export default Card;
